        bool success = await terminalStateService.DeleteTerminalStateAsync(terminalState.ID);
        if (!success)
        {
            await JsRuntime.InvokeVoidAsync("alert", "OcurriÃ³ un error al eliminar");
            return;
        }

        terminalStates = await terminalStateService.GetTerminalStates();
        Timer timer = new Timer();
        timer.Interval = 1000;
        int countDown = 5;
        timer.Elapsed += (o, e) =>
        {
            if (countDown <= 0)
            {
                showAlert = false;
                timer.Dispose();
                StateHasChanged();
            }
            else
            {
                countDown -= 1;
                StateHasChanged();
            }
        };
        timer.Start();
        showAlert = true;
        StateHasChanged();
    }
    private async Task buscar()
